import React from 'react';
import { Players } from './players';
import axios from 'axios';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

export class Read extends React.Component {

    constructor(){
        super();
        this.ReloadData = this.ReloadData.bind(this);
    }

    state = {
        players: []
    };

    ReloadData(){
        axios.get('http://localhost:4000/api/players')
            .then((response) => {
                this.setState({ players: response.data })
            })
            .catch((error) => {
                console.log(error)
            });
    }

    componentDidMount() {
        axios.get('http://localhost:4000/api/players')
            .then((response) => {
                this.setState({ players: response.data })
            })
            .catch((error) => {
                console.log(error)
            });
    }

    render() {
        return (
            <div>
                <h1>Make changes to your team here</h1>
                <Players players={this.state.players} ReloadData={this.ReloadData}></Players>
                
                <Card>
                    <Card.Header>{this.props.player.Name}</Card.Header>
                    <Card.Body>
                        <blockquote className="blockquote mb-0">
                            <img src={this.props.player.Poster} width="200" height="200"></img>
                            <footer className="blockquote-footer">
                                {this.props.player.Age}<br />
                                {this.props.player.Team}

                            </footer>
                        </blockquote>
                    </Card.Body>
                    <Button variant="danger" onClick={this.DeletePlayer}>Delete</Button>
                </Card>
            </div>
            
        );
    }
}
